module.exports = function(gui) {
    this.menu = new gui.Menu({'type': 'menubar'});
    this.fileMenu = new gui.Menu();
    this.helpMenu = new gui.Menu();
    this.projectMenu = new gui.Menu();

    // file menu part
    this.fileMenu.append(new gui.MenuItem({label: 'New Project',  modifiers: "ctrl-n"}));
    this.fileMenu.append(new gui.MenuItem({label: 'Open Project', modifiers: "ctrl-o"}));
    this.fileMenu.append(new gui.MenuItem({label: 'Save', modifiers: "ctrl-s"}));
    this.fileMenu.append(new gui.MenuItem({label: 'Quit'}));

    // file menu action
    this.fileMenu.items[0].click = function() {
        window.alert('create new project');
    };
    this.fileMenu.items[1].click = function() {
        window.alert('add exist project');
    }
    this.fileMenu.items[3].click = function() {
        gui.Window.get().close(); 
    };

    // help menu part
    this.helpMenu.append(new gui.MenuItem({label: 'Gulp Doc'}));
    this.helpMenu.append(new gui.MenuItem({label: 'Check for Update'}));
    this.helpMenu.append(new gui.MenuItem({label: 'About'}));

    // help menus action
    this.helpMenu.items[0].click = function() {
        gui.Window.open("http://gulpjs.com/");
    };
    this.helpMenu.items[1].click = function() {
        window.alert('check for update');
    };
    this.helpMenu.items[2].click = function() {
        window.alert('Gulp IDE v0.0.1');
    };
    
    // project list right click menu
    this.projectMenu.append(new gui.MenuItem({label: 'Remove From Projects'}));

    // window menus
    this.menu.append(new gui.MenuItem(
        {
            label: "File",
            submenu: this.fileMenu
        }
    ));
    this.menu.append(new gui.MenuItem(
        {
            label: "Help",
            submenu: this.helpMenu
        }
    ));
};
